import React, { useState, useEffect } from 'react';
import axios from 'axios';
import TaskItem from './TaskItem';
import AddTask from './AddTask';

const TaskList = () => {
    const [tasks, setTasks] = useState([]);

    useEffect(() => {
        axios.get('http://localhost:5000/tasks')
            .then((response) => setTasks(response.data))
            .catch((error) => console.error('Error fetching tasks:', error));
    }, []);

    const handleAddTask = (newTask) => {
        axios.post('http://localhost:5000/tasks', newTask)
            .then((response) => setTasks([...tasks, response.data]))
            .catch((error) => console.error('Error adding task:', error));
    };

    const handleDeleteTask = (taskId) => {
        axios.delete(`http://localhost:5000/tasks/${taskId}`)
            .then(() => setTasks(tasks.filter((task) => task.task_id !== taskId)))
            .catch((error) => console.error('Error deleting task:', error));
    };

    return (
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start', gap: '20px' }}>
            <div style={{ flex: '1', maxWidth: '400px' }}>
                <AddTask onAddTask={handleAddTask} />
            </div>
            <div style={{ flex: '2', maxWidth: '800px' }}>
                <h2 style={{ textAlign: 'center', color: '#1976d2' }}>Tasks</h2>
                <ul style={{ listStyle: 'none', padding: '0' }}>
                    {tasks.map((task) => (
                        <li key={task.task_id} style={{ marginBottom: '15px' }}>
                            <TaskItem task={task} onDeleteTask={handleDeleteTask} />
                        </li>
                    ))}
                </ul>
            </div>
        </div>
    );
};

export default TaskList;
