import React from 'react';
import TaskList from './components/TaskList'; // הצגת רשימת המשימות
import { CssBaseline, ThemeProvider, createTheme } from '@mui/material';
import './App.css';

// הגדרת תבנית צבעים בהירה
const theme = createTheme({
    palette: {
        mode: 'light', // מצב בהיר
        primary: {
            main: '#4caf50', // ירוק כהה
        },
        secondary: {
            main: '#1976d2', // כחול כהה
        },
        background: {
            default: '#ffffff', // רקע לבן
            paper: '#f4f6f8', // רקע בהיר יותר עבור אלמנטים
        },
        text: {
            primary: '#333', // טקסט כהה
            secondary: '#555', // טקסט משנה בגוון אפור
        },
    },
});

function App() {
    return (
        <ThemeProvider theme={theme}>
            <CssBaseline />
            <div className="App">
                {/* הצגת רק את רכיב TaskList */}
                <TaskList />
            </div>
        </ThemeProvider>
    );
}

export default App;
