import React, { useState, useEffect } from 'react';
import { Box, TextField, Button, MenuItem, Select, InputLabel, FormControl, Typography } from '@mui/material';
import TaskItem from './TaskItem'; // הצגת כל משימה ברשימה
import axios from 'axios';

const TaskList = () => {
    const [tasks, setTasks] = useState([]);
    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');
    const [dueDate, setDueDate] = useState('');
    const [priorityId, setPriorityId] = useState(1);
    const [statusId, setStatusId] = useState(1);
    const [assignedUserId, setAssignedUserId] = useState('');

    useEffect(() => {
        axios.get('http://localhost:5000/tasks')
            .then(response => {
                setTasks(response.data);
            })
            .catch(error => console.error('Error fetching tasks:', error));
    }, []);

    const handleSubmit = (e) => {
        e.preventDefault();
        const newTask = {
            title,
            description,
            due_date: dueDate,
            priority_id: parseInt(priorityId, 10),
            status_id: parseInt(statusId, 10),
            assigned_user_id: parseInt(assignedUserId, 10) || null,
        };

        // שליחה ל-API להוספת המשימה
        axios.post('http://localhost:5000/tasks', newTask)
            .then(response => {
                setTasks([...tasks, response.data]); // הוספת המשימה החדשה לרשימה
                setTitle('');
                setDescription('');
                setDueDate('');
                setPriorityId(1);
                setStatusId(1);
                setAssignedUserId('');
            })
            .catch(error => console.error('Error adding task:', error));
    };

    const handleDeleteTask = (taskId) => {
        axios.delete(`http://localhost:5000/tasks/${taskId}`)
            .then(() => {
                const updatedTasks = tasks.filter(task => task.task_id !== taskId);
                setTasks(updatedTasks);
            })
            .catch(error => console.error('Error deleting task:', error));
    };

    return (
        <Box className="task-list-container" display="flex" justifyContent="space-between" gap={2}>
            {/* טופס הוספת משימה חדשה בצד שמאל */}
            <Box className="add-task-container" width="45%">
                <Typography variant="h4" gutterBottom>New Task</Typography>
                <form onSubmit={handleSubmit}>
                    <TextField
                        label="Title"
                        variant="outlined"
                        fullWidth
                        value={title}
                        onChange={(e) => setTitle(e.target.value)}
                        required
                        margin="normal"
                    />
                    <TextField
                        label="Description"
                        variant="outlined"
                        fullWidth
                        multiline
                        rows={4}
                        value={description}
                        onChange={(e) => setDescription(e.target.value)}
                        margin="normal"
                    />
                    <TextField
                        label="Due Date"
                        type="date"
                        variant="outlined"
                        fullWidth
                        value={dueDate}
                        onChange={(e) => setDueDate(e.target.value)}
                        margin="normal"
                        InputLabelProps={{
                            shrink: true,
                        }}
                    />
                    <FormControl fullWidth margin="normal">
                        <InputLabel>Priority</InputLabel>
                        <Select
                            value={priorityId}
                            onChange={(e) => setPriorityId(e.target.value)}
                            label="Priority"
                        >
                            <MenuItem value={1}>Low</MenuItem>
                            <MenuItem value={2}>Medium</MenuItem>
                            <MenuItem value={3}>High</MenuItem>
                            <MenuItem value={4}>Urgent</MenuItem>
                        </Select>
                    </FormControl>
                    <FormControl fullWidth margin="normal">
                        <InputLabel>Status</InputLabel>
                        <Select
                            value={statusId}
                            onChange={(e) => setStatusId(e.target.value)}
                            label="Status"
                        >
                            <MenuItem value={1}>Draft</MenuItem>
                            <MenuItem value={2}>In Progress</MenuItem>
                            <MenuItem value={3}>On Hold</MenuItem>
                            <MenuItem value={4}>Completed</MenuItem>
                            <MenuItem value={5}>Deleted</MenuItem>
                        </Select>
                    </FormControl>
                    <TextField
                        label="Assigned User ID"
                        type="number"
                        variant="outlined"
                        fullWidth
                        value={assignedUserId}
                        onChange={(e) => setAssignedUserId(e.target.value)}
                        margin="normal"
                    />
                    <Button variant="contained" color="primary" type="submit" fullWidth>
                        Add Task
                    </Button>
                </form>
            </Box>

            {/* הצגת המשימות בצד ימין */}
            <Box className="task-list-display" width="50%">
                <Typography variant="h4" gutterBottom>Task List</Typography>
                {tasks.map(task => (
                    <TaskItem
                        key={task.task_id}
                        task={task}
                        onDeleteTask={handleDeleteTask}
                    />
                ))}
            </Box>
        </Box>
    );
};

export default TaskList;
