import React, { useEffect } from 'react';
// ייבוא Firebase ו-FirebaseUI
import { initializeApp } from 'firebase/app';
import { getAuth, GoogleAuthProvider, EmailAuthProvider } from 'firebase/auth';
import firebase from 'firebase/compat/app';
import 'firebaseui/dist/firebaseui.css';
import * as firebaseui from 'firebaseui';

// אתחול Firebase עם הקונפיגורציה שלך
const firebaseConfig = {
    apiKey: "AIzaSyCVs--q12ZT80sGUZhX5K5-PNEVuczoA_o",
    authDomain: "full-stack-tasks.firebaseapp.com",
    projectId: "full-stack-tasks",
    storageBucket: "full-stack-tasks.firebasestorage.app",
    messagingSenderId: "949925090872",
    appId: "1:949925090872:web:604557e149311b1ed7b8cd",
    measurementId: "G-M428T9CXVC"
  };

// אתחול האפליקציה של Firebase
initializeApp(firebaseConfig);

const FirebaseAuthUI = () => {
    useEffect(() => {
        const uiConfig = {
            signInSuccessUrl: '/',
            signInOptions: [
                GoogleAuthProvider.PROVIDER_ID,
                EmailAuthProvider.PROVIDER_ID,
            ],
        };

        // יצירת מופע של FirebaseUI
        const ui = new firebaseui.auth.AuthUI(getAuth());

        // אתחול ה-UI
        ui.start('#firebaseui-auth-container', uiConfig);

        // לנקות את ה-UI לאחר שהקומפוננטה תסיים לפעול
        return () => {
            ui.reset();
        };
    }, []); // הפעולה מתבצעת פעם אחת רק אחרי העלאת הקומפוננטה

    return <div id="firebaseui-auth-container"></div>;
};

export default FirebaseAuthUI;
