import React from 'react';
import { Card, CardContent, Typography, Button } from '@mui/material';

const TaskItem = ({ task, onDeleteTask }) => {
    return (
        <Card sx={{ marginBottom: 2 }}>
            <CardContent>
                <Typography variant="h6">{task.title}</Typography>
                <Typography variant="body2">{task.description}</Typography>
                <Typography variant="body2" color="textSecondary">Due: {task.due_date}</Typography>
                <Typography variant="body2" color="textSecondary">Priority: {task.priority}</Typography>
                <Typography variant="body2" color="textSecondary">Status: {task.status}</Typography>
                <Button onClick={() => onDeleteTask(task.task_id)} color="secondary">Delete</Button>
            </CardContent>
        </Card>
    );
};

export default TaskItem;
