import React from 'react';
import { Card, CardContent, Typography, Button } from '@mui/material';
import './TaskItem.css';

const TaskItem = ({ task, onDeleteTask }) => {
    const priorityLabels = ['Low', 'Medium', 'High', 'Urgent'];
    const statusLabels = ['Draft', 'In Progress', 'On Hold', 'Completed', 'Deleted'];

    return (
        <Card className="task-item">
            <CardContent>
                <Typography variant="h6">{task.title}</Typography>
                <Typography variant="body1" color="textSecondary">{task.description}</Typography>
                <Typography variant="body2">Priority: {priorityLabels[task.priority_id - 1]}</Typography>
                <Typography variant="body2">Status: {statusLabels[task.status_id - 1]}</Typography>
                <Typography variant="body2">Due Date: {task.due_date || 'N/A'}</Typography>
                <Typography variant="body2">Assigned User ID: {task.assigned_user_id || 'N/A'}</Typography>
                <Button 
                    variant="contained" 
                    color="secondary" 
                    onClick={() => onDeleteTask(task.task_id)}
                >
                    Delete
                </Button>
            </CardContent>
        </Card>
    );
};

export default TaskItem;
