import React, { useState, useEffect } from 'react';
import TaskItem from './TaskItem';
import AddTask from './AddTask';
import axios from 'axios';
import './TaskList.css';

const TaskList = ({ user }) => {
    const [tasks, setTasks] = useState([]);

    // המתנה שהמשתמש יהיה זמין לפני המשך הטעינה
    useEffect(() => {
        if (!user) {
            return; // לא לטעון משימות אם המשתמש לא מחובר
        }

        // טוען את המשימות רק אחרי שהמשתמש זמין
        axios.get(`http://localhost:5000/tasks?userId=${user.uid}`)
            .then(response => {
                setTasks(response.data);
            })
            .catch(error => console.error('Error fetching tasks:', error));
    }, [user]); // כל פעם שהמשתמש משתנה, הטעינה תתבצע

    if (!user) {
        return <div>Loading...</div>; // הצגת טעינה עד שהמשתמש יתחבר
    }

    // הוספת משימה חדשה
    const handleAddTask = (newTask) => {
        axios.post('http://localhost:5000/tasks', { ...newTask, userId: user.uid })
            .then(response => {
                setTasks([...tasks, response.data]);
            })
            .catch(error => console.error('Error adding task:', error));
    };

    // עדכון משימה
    const handleEditTask = (updatedTask) => {
        axios.put(`http://localhost:5000/tasks/${updatedTask.task_id}`, updatedTask)
            .then(response => {
                const updatedTasks = tasks.map(task =>
                    task.task_id === updatedTask.task_id ? response.data : task
                );
                setTasks(updatedTasks);  // עדכון המשימות במצב
            })
            .catch(error => console.error('Error updating task:', error));
    };

    // מחיקת משימה
    const handleDeleteTask = (taskId) => {
        axios.delete(`http://localhost:5000/tasks/${taskId}`)
            .then(() => {
                const updatedTasks = tasks.filter(task => task.task_id !== taskId);
                setTasks(updatedTasks);
            })
            .catch(error => console.error('Error deleting task:', error));
    };

    return (
        <div className="task-list-container">
            <div className="add-task-container">
                <AddTask onAddTask={handleAddTask} />
            </div>
            <div className="task-list-display">
                {tasks.map(task => (
                    <TaskItem
                        key={task.task_id}
                        task={task}
                        onDeleteTask={handleDeleteTask}
                        onEditTask={handleEditTask}
                    />
                ))}
            </div>
        </div>
    );
};

export default TaskList;
