import React from 'react';
import './TaskItem.css'; // ייבוא קובץ ה-CSS של TaskItem

const TaskItem = ({ task, onDeleteTask }) => {
    const priorityLabels = ['Low', 'Medium', 'High', 'Urgent'];
    const statusLabels = ['Draft', 'In Progress', 'On Hold', 'Completed', 'Deleted'];

    return (
        <li className="task-item">
            <h3>{task.title}</h3>
            <p>{task.description}</p>
            <p>Priority: {priorityLabels[task.priority_id - 1]}</p>
            <p>Status: {statusLabels[task.status_id - 1]}</p>
            <p>Due Date: {task.due_date || 'N/A'}</p>
            <p>Assigned User ID: {task.assigned_user_id || 'N/A'}</p>
            <button onClick={() => onDeleteTask(task.task_id)}>Delete</button>
        </li>
    );
};

export default TaskItem;
