import React, { useEffect, useState } from 'react';
import TaskList from './components/TaskList';
import { CssBaseline, ThemeProvider, createTheme, Typography, Box } from '@mui/material';
import './App.css';
import FirebaseAuthUI from './components/FirebaseAuthUI'; // הוספת FirebaseAuthUI

// הגדרת תבנית צבעים בהירה
const theme = createTheme({
    palette: {
        mode: 'light', // מצב בהיר
        primary: {
            main: '#4caf50', // ירוק כהה
        },
        secondary: {
            main: '#1976d2', // כחול כהה
        },
        background: {
            default: '#ffffff', // רקע לבן
            paper: '#f4f6f8', // רקע בהיר יותר עבור אלמנטים
        },
        text: {
            primary: '#333', // טקסט כהה
            secondary: '#555', // טקסט משנה בגוון אפור
        },
    },
    typography: {
        h1: {
            fontSize: '2.5rem',
            fontWeight: 700,
            textAlign: 'center',
            color: '#333', // צבע טקסט כהה לכותרת
            marginBottom: '20px',
            marginTop: '40px',
        },
    },
});

function App() {
    const [user, setUser] = useState(null);

    useEffect(() => {
        const unsubscribe = auth.onAuthStateChanged(setUser);
        return () => unsubscribe();
    }, []);

    return (
        <ThemeProvider theme={theme}>
            <CssBaseline />
            <div className="App">
                {/* הצגת הכותרת */}
                <Box>
                    <Typography variant="h1">To-Do List</Typography>
                </Box>

                {/* אם המשתמש לא מחובר, נציג את ממשק ההתחברות */}
                {!user ? (
                    <FirebaseAuthUI />
                ) : (
                    <div>
                        {/* הצגת המשימות של המשתמש */}
                        <TaskList user={user} />
                    </div>
                )}
            </div>
        </ThemeProvider>
    );
}

export default App;
