import React from 'react';
import TaskList from './components/TaskList';
import { CssBaseline, ThemeProvider, createTheme, Typography, Box } from '@mui/material';
import './App.css';

// הגדרת תבנית צבעים מותאמת אישית
const theme = createTheme({
    palette: {
        primary: {
            main: '#3f51b5', // צבע ראשי
        },
        secondary: {
            main: '#f50057', // צבע משני
        },
    },
    typography: {
        h1: {
            fontSize: '2rem', // גודל גופן לכותרת ראשית
            fontWeight: 'bold', // משקל גופן
            textAlign: 'center', // יישור הכותרת לאמצע
            color: '#3f51b5', // צבע גופן
            marginBottom: '20px', // רווח מלמטה
            marginTop: '20px', // רווח מלמעלה
        },
    },
});

function App() {
    return (
        <ThemeProvider theme={theme}>
            <CssBaseline />
            <div className="App">
                {/* הוספת כותרת בעיצוב מותאם אישית */}
                <Box>
                    <Typography variant="h1">To-Do List</Typography>
                </Box>
                <TaskList />
            </div>
        </ThemeProvider>
    );
}

export default App;
