import React from 'react';
import TaskList from './components/TaskList';
import AddTask from './components/AddTask';
import { CssBaseline, ThemeProvider, createTheme, Typography, Box } from '@mui/material';
import './App.css';

// הגדרת תבנית צבעים בהירה
const theme = createTheme({
    palette: {
        mode: 'light', // מצב בהיר
        primary: {
            main: '#4caf50', // ירוק כהה
        },
        secondary: {
            main: '#1976d2', // כחול כהה
        },
        background: {
            default: '#ffffff', // רקע לבן
            paper: '#f4f6f8', // רקע בהיר יותר עבור אלמנטים
        },
        text: {
            primary: '#333', // טקסט כהה
            secondary: '#555', // טקסט משנה בגוון אפור
        },
    },
    typography: {
        h1: {
            fontSize: '2.5rem',
            fontWeight: 700,
            textAlign: 'center',
            color: '#333', // צבע טקסט כהה לכותרת
            marginBottom: '20px',
            marginTop: '40px',
        },
    },
});

  // הוספת משימה חדשה
  const handleAddTask = (newTask) => {
    axios.post('http://localhost:5000/tasks', newTask)
        .then(response => {
            setTasks([...tasks, response.data]);
        })
        .catch(error => console.error('Error adding task:', error));
};

function App() {
    return (
        <ThemeProvider theme={theme}>
            <CssBaseline />
            <div className="App">
                {/* כותרת TODO LIST */}
                <Box className="add-task-container">
                    <Typography variant="h1">To-Do List</Typography>
                    {/* הוספת טופס המשימה החדשה */}
                    <AddTask onAddTask={handleAddTask} />
                </Box>
                {/* המשימות */}
                <Box className="task-list-container">
                    <TaskList />
                </Box>
            </div>
        </ThemeProvider>
    );
}

export default App;
