import React, { useState } from 'react';
import { Button, Paper, Typography, Box, TextField } from '@mui/material';

const TaskItem = ({ task, onDeleteTask, onUpdateTask }) => {
    const [isEditing, setIsEditing] = useState(false);
    const [editedTask, setEditedTask] = useState({ ...task });

    const priorityLabels = ['Low', 'Medium', 'High', 'Urgent'];
    const statusLabels = ['Draft', 'In Progress', 'On Hold', 'Completed', 'Deleted'];

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setEditedTask((prevTask) => ({ ...prevTask, [name]: value }));
    };

    const handleSave = () => {
        onUpdateTask(editedTask);
        setIsEditing(false);
    };

    return (
        <Paper
            elevation={3}
            sx={{
                padding: '20px',
                borderRadius: '15px',
                backgroundColor: '#ffffff',
                border: '1px solid #1976d2',
            }}
        >
            {!isEditing ? (
                <Box>
                    <Typography variant="h6" sx={{ fontWeight: 'bold', color: '#4caf50' }}>
                        {task.title}
                    </Typography>
                    <Typography variant="body2" sx={{ color: '#555' }}>
                        {task.description}
                    </Typography>
                    <Typography sx={{ fontSize: '1rem', marginTop: '10px', color: '#333' }}>
                        <strong>Priority:</strong> {priorityLabels[task.priority_id - 1]}
                    </Typography>
                    <Typography sx={{ fontSize: '1rem', color: '#333' }}>
                        <strong>Status:</strong> {statusLabels[task.status_id - 1]}
                    </Typography>
                    <Typography sx={{ fontSize: '1rem', color: '#333' }}>
                        <strong>Due Date:</strong> {task.due_date || 'N/A'}
                    </Typography>
                    <Typography sx={{ fontSize: '1rem', color: '#333' }}>
                        <strong>Assigned User ID:</strong> {task.assigned_user_id || 'N/A'}
                    </Typography>
                    <Button
                        onClick={() => setIsEditing(true)}
                        variant="outlined"
                        color="primary"
                        sx={{
                            marginTop: '10px',
                            marginRight: '10px',
                            borderRadius: '8px',
                            padding: '8px 15px',
                        }}
                    >
                        Edit
                    </Button>
                    <Button
                        onClick={() => onDeleteTask(task.task_id)}
                        variant="contained"
                        color="secondary"
                        sx={{
                            marginTop: '10px',
                            borderRadius: '8px',
                            padding: '8px 15px',
                        }}
                    >
                        Delete
                    </Button>
                </Box>
            ) : (
                <Box>
                    <TextField
                        label="Title"
                        name="title"
                        value={editedTask.title}
                        onChange={handleInputChange}
                        fullWidth
                        sx={{ marginBottom: '10px' }}
                    />
                    <TextField
                        label="Description"
                        name="description"
                        value={editedTask.description}
                        onChange={handleInputChange}
                        fullWidth
                        multiline
                        rows={2}
                        sx={{ marginBottom: '10px' }}
                    />
                    <TextField
                        label="Due Date"
                        name="due_date"
                        type="date"
                        value={editedTask.due_date}
                        onChange={handleInputChange}
                        fullWidth
                        sx={{ marginBottom: '10px' }}
                        InputLabelProps={{ shrink: true }}
                    />
                    <Button
                        onClick={handleSave}
                        variant="contained"
                        color="primary"
                        sx={{
                            marginTop: '10px',
                            marginRight: '10px',
                            borderRadius: '8px',
                            padding: '8px 15px',
                        }}
                    >
                        Save
                    </Button>
                    <Button
                        onClick={() => setIsEditing(false)}
                        variant="outlined"
                        color="secondary"
                        sx={{
                            marginTop: '10px',
                            borderRadius: '8px',
                            padding: '8px 15px',
                        }}
                    >
                        Cancel
                    </Button>
                </Box>
            )}
        </Paper>
    );
};

export default TaskItem;
