import React from 'react';
import TaskList from './components/TaskList';
import { CssBaseline, ThemeProvider, createTheme, Typography, Box } from '@mui/material';
import './App.css';

// הגדרת תבנית צבעים עם צבעים רכים ומודרניים
const theme = createTheme({
    palette: {
        primary: {
            main: '#4caf50', // צבע ירוק עז
        },
        secondary: {
            main: '#ff9800', // צבע כתום רך
        },
        background: {
            default: '#f7f7f7', // רקע בהיר ורגוע
        },
    },
    typography: {
        h1: {
            fontSize: '2.5rem', // גודל גופן לכותרת
            fontWeight: 700, // כותרת מודגשת
            textAlign: 'center', // יישור לאמצע
            color: '#333', // צבע כהה לכותרת
            marginBottom: '20px',
            marginTop: '40px', // רווח למעלה
        },
    },
});

function App() {
    return (
        <ThemeProvider theme={theme}>
            <CssBaseline />
            <div className="App">
                {/* כותרת עם עיצוב חדש */}
                <Box>
                    <Typography variant="h1">To-Do List</Typography>
                </Box>
                <TaskList />
            </div>
        </ThemeProvider>
    );
}

export default App;
