import React, { useEffect, useState } from 'react';
import firebase from 'firebase/app';
import 'firebase/auth';
import firebaseui from 'firebaseui';
import { auth } from '../config/firebase-config'; // הגישה ל-config/firebase-config.js

const FirebaseAuthUI = () => {
    const [user, setUser] = useState(null);
    
    useEffect(() => {
        const ui = new firebaseui.auth.AuthUI(auth);

        // הגדרת אפשרויות FirebaseUI
        const uiConfig = {
            signInSuccessUrl: '/',  // הכתובת שתועבר אליה לאחר התחברות
            signInOptions: [
                firebase.auth.EmailAuthProvider.PROVIDER_ID,  // התחברות דרך אימייל
                firebase.auth.GoogleAuthProvider.PROVIDER_ID, // התחברות דרך Google
                firebase.auth.FacebookAuthProvider.PROVIDER_ID // התחברות דרך Facebook
            ],
            tosUrl: 'https://www.example.com/terms',  // תנאי שימוש (אם יש)
            privacyPolicyUrl: 'https://www.example.com/privacy'  // מדיניות פרטיות (אם יש)
        };

        // אתחול FirebaseUI
        ui.start('#firebaseui-auth-container', uiConfig);

        // מעקב אחרי שינוי במצב המשתמש
        const unsubscribe = auth.onAuthStateChanged(setUser);

        return () => unsubscribe();
    }, []);

    const handleLogout = () => {
        auth.signOut();
    };

    return (
        <div>
            {!user ? (
                <div id="firebaseui-auth-container"></div> // אלמנט שבו יופיע FirebaseUI
            ) : (
                <div>
                    <h1>שלום, {user.displayName}</h1>
                    <button onClick={handleLogout}>התנתקות</button>
                </div>
            )}
        </div>
    );
};

export default FirebaseAuthUI;
